import logo from "./logo.svg";
import "./App.css";

function App() {
  const handleInstall = () => {
    console.log("Install");
  };

  const handleOpen = () => {
    function openAmazonApp() {
      var url = "amzn://";
      var appStoreUrl = "https://apps.apple.com/us/app/amazon-shopping/id297606951";

      // Try to open the app
      window.location.href = url;

      // Set a timeout to redirect to the App Store if the app is not opened
      setTimeout(function() {
          window.location.href = appStoreUrl;
      }, 2000); // Adjust time as needed
  }
  };

  return (
    <div className="App">
      <img
        src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTy8L1HIH2ZlhTcSR2x5c993GIA6DFFs06YEg&s"
        alt=""
      />
      <div className="button-container">
        {/* <button className="btn" onClick={handleInstall}>Install the app</button> */}
        <button className="btn2" onClick={handleOpen}>
          Open the app
        </button>
      </div>
    </div>
  );
}

export default App;
